        R.drawable.sembilan_thumbnail,
        R.drawable.sepuluh_thumbnail,
        R.drawable.sebelas_thumbnail,
        R.drawable.duabelas_thumbnail
    )

    private val imageLarge = intArrayOf(
        R.drawable.satu,
        R.drawable.dua,
        R.drawable.tiga,
        R.drawable.empat,
        R.drawable.lima,
        R.drawable.enam,
        R.drawable.tujuh,
        R.drawable.delapan,
        R.drawable.sembilan,
        R.drawable.sepuluh,
        R.drawable.sebelas,
        R.drawable.duabelas
    )


    val listData: ArrayList<News>
        get() {
            val list = arrayListOf<News>()
            for (position in newsTitle.indices) {
                val news = News()
                news.title = newsTitle[position]
                news.content = newsDetails[position]
                news.category = newsCategory[position]
                news.date = newsDate[position]
                news.imgThumbnail = imageThumbnail[position]
                news.imgLarge = imageLarge[position]
                list.add(news)
            }
            return list
        }
}